let navToggle=document.querySelector(".nav__toggle"),navWrapper=document.querySelector(".nav__wrapper"),searchToggle=document.querySelector(".search__toggle"),searchForm=document.querySelector(".search__form");function showNav(){navWrapper.classList.toggle("active")}function showSearch(){searchForm.classList.toggle("active"),searchToggle.classList.toggle("active"),searchToggle.classList.contains("active")?searchToggle.setAttribute("aria-label","Close search"):searchToggle.setAttribute("aria-label","Open search")}navToggle.addEventListener("click",showNav),searchToggle.addEventListener("click",showSearch);